            CREATE TABLE salary(
				salaryID varchar(10) not null,
				employeeID varchar(10) not null,
				employeeName varchar(20),
				month varchar(10),
				date  date,
				amount real,
				primary key (salaryID)
			)
        
	

	
	
                 
            DROP TABLE IF EXISTS salary 
        
	

	
	
        
